CREATE TABLE IF NOT EXISTS MHPLINK_PRODUCT (ID BIGINT NOT NULL AUTO_INCREMENT, NAME VARCHAR(18) NOT NULL, DESCRIPTION VARCHAR(250), BRANDNAME VARCHAR(80) NOT NULL, PRODUCTURL VARCHAR(100) NOT NULL, REPORTFRAUDURL VARCHAR(100), SHOWVALIDATION BOOLEAN);
CREATE TABLE IF NOT EXISTS MHPLINK_PROPERTY (ID BIGINT NOT NULL AUTO_INCREMENT, NAME VARCHAR(80) NOT NULL, UID VARCHAR(80) NOT NULL, VALUE VARCHAR(250) NOT NULL);